    private void btnCleanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCleanActionPerformed
        txtUser.setText("");
        txtPassword.setText("");
        txtMensaje.setText("");
    }//GEN-LAST:event_btnCleanActionPerformed

    private void btnLoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLoginActionPerformed
        String usuario = txtUser.getText();
        String contrasenia = txtPassword.getText();
        Usuario usr = control.validarUsuario(usuario, contrasenia);
        
        if(usr != null){
            String rol = usr.getRol().getNombreRol();
            if(rol.equals("admin")){
                PrincipalAdmin pAdmin = new PrincipalAdmin(control, usr);
                pAdmin.setVisible(true);
                pAdmin.setLocationRelativeTo(null);
                this.dispose();
            }
            if(rol.equals("user")){
                PrincipalUser pUser = new PrincipalUser(control, usr);
                pUser.setVisible(true);
                pUser.setLocationRelativeTo(null);
                this.dispose();
            }
        } else {
            txtMensaje.setText("Usuario y/o contrase√±a incorrecta(s)");
        }
        
        
        
        
    }//GEN-LAST:event_btnLoginActionPerformed


